import json
import java.io
from org.apache.commons.io import IOUtils
from java.nio.charset import StandardCharsets
from org.apache.nifi.processor.io import StreamCallback

class ModJSON(StreamCallback):
  def __init__(self):
        pass
  def process(self, inputStream, outputStream):
    text = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
    obj = json.loads(text)
    avgPressure = float(obj['avgpressure'])
    stdDev = float(obj['stdpressure'])*2
    pressureTop = avgPressure+stdDev
    pressureBottom = avgPressure-stdDev
    if float(obj['pressure'])>pressureTop or float(obj['pressure']) < pressureBottom:
    	newObj = {"type": "object",
    	"properties": {
        "ID": {
            "type": 432
        },
        "skidName": {
            "type": obj['wellname']
        },
        "alertMessage": {
            "type": "Pressure Alert"
        }
    }
}
    	outputStream.write(json.dumps(newObj, indent=4).encode('utf-8'))

flowFile = session.get()
if (flowFile != None):
  flowFile = session.write(flowFile, ModJSON())
  flowFile = session.putAttribute(flowFile, "filename", flowFile.getAttribute('filename').split('.')[0]+'_translated.json')
session.transfer(flowFile, REL_SUCCESS)
session.commit()

